import React from 'react';
import './Home.css';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div className="home-container">
      <img src="/image.png" alt="Pranav" className="profile-picture" />
      <div className="hero-content">

        <div className="text-content">
          <p id="intro">Hi, my name is </p>
          <h1>Pranav Malhotra</h1>
          <p>
            I'm a software engineer specializing in building and designing innovative digital experiences. Currently, I am focused on learning about NextJS, TypeScript, Postgres, NodeJS, Angular, MongoDB and Firebase by implementing them in my projects.{' '}
            <Link to="/projects" className="link-to-projects">Here</Link> are some of those.
          </p>
          <br />
          <p>HTML | CSS | JavaScript | React | React Native | SQL | AWS | UI/UX | JENKINS | JIRA | AGILE</p>
          <div className="cta-buttons">
            <Link to="/experience" className="btn">Learn More</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
