import React, { useEffect } from 'react';
import './Project.css';

const Project = () => {
  const projects = [
    { title: "Tourism Website", description: "Description of Project One." },
    { title: "Jeopardy Quiz Game", description: "Description of Project Two." },
    { title: "Dragon Ball Game", description: "Description of Project Three." },
    { title: "Dictionary", description: "Description of Project Four." },
    { title: "Pet Adoption App", description: "Description of Project Five." },
    { title: "To-Do List", description: "Description of Project Six." },
    { title: "Translator App", description: "Description of Project Seven." },
    { title: "Explorez - Travel App", description: "Description of Project Eight." },
    { title: "Weather Site", description: "Description of Project Nine." },
    { title: "Youtube Clone", description: "Description of Project Ten." },
  ];

  useEffect(() => {
    const projectItems = document.querySelectorAll('.project-item');
    projectItems.forEach(item => {
      const randomWidth = Math.floor(Math.random() * 40) + 30; // Random width between 30% and 70%
      item.style.flex = `1 1 ${randomWidth}%`;
    });
  }, []);

  return (
    <div className="projpage-container">
      <h1 className="project-title">Projects</h1>

      <div className="projects-grid">
        {projects.map((project, index) => (
          <div className="project-item" key={index}>
            <h2>{project.title}</h2>
            <p>{project.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Project;
