import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import { Box, Button, Container, Heading, Link, Text, VStack } from '@chakra-ui/react';
import { motion } from 'framer-motion';

const MotionBox = motion(Box);
const MotionText = motion(Text);
const MotionHeading = motion(Heading);
const MotionButton = motion(Button);

const Home = () => {
  return (
    <MotionBox
      bg="black"
      color="white"
      minH="90vh"
      display="flex"
      alignItems="center"
      justifyContent="center"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <Container
        maxW="container.md"
        p={8}
        boxShadow="xl"
        borderRadius="md"
        borderColor="#04D9FF"
        borderWidth="1px"
      >
        <VStack spacing={6} align="start">
          <MotionText
            id="intro"
            color="gray.400"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Hi, my name is
          </MotionText>
          <MotionHeading
            as="h1"
            size="2xl"
            color="#04D9FF"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            Pranav Malhotra
          </MotionHeading>
          <MotionText
            fontSize="lg"
            lineHeight="tall"
            color="gray.400"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            I'm a software engineer specializing in building and designing innovative digital experiences. Currently, I am focused on learning about NextJS, TypeScript, Postgres, NodeJS, Angular, MongoDB and Firebase by implementing them in my projects.{' '}
            <Link as={RouterLink} to="/projects" color="#04D9FF">Here</Link> are some of those.
          </MotionText>
          <MotionText
            fontSize="lg"
            color="gray.400"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            HTML | CSS | JavaScript | React | React Native | SQL | AWS | UI/UX | JENKINS | JIRA | AGILE
          </MotionText>
          <MotionButton
            as={RouterLink}
            to="/experience"
            colorScheme="teal"
            variant="outline"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1 }}
          >
            Learn More
          </MotionButton>
        </VStack>
      </Container>
    </MotionBox>
  );
}

export default Home;
