import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Skeleton from 'react-loading-skeleton'; // Import Skeleton from react-loading-skeleton
import './Home.css';

const Home = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading delay for demo purposes
    setTimeout(() => {
      setIsLoading(false);
    }, 1500); // Adjust as needed
  }, []);

  return (
    <div className="home-container">
      <div className="hero-content">
        {/* Conditional rendering of skeleton loader */}
        {isLoading ? (
          <>
            <Skeleton circle width={300} height={700} /> {/* Example skeleton for profile picture */}
            <div className="text-content">
              <Skeleton width={'100%'} height={40} />
              <Skeleton count={5} />
            </div>
          </>
        ) : (
          <>
            <img src="/IMG_6460.png" alt="Pranav" className="profile-picture" />
            <div className="text-content">
              <p id="intro">Hi, my name is </p>
              <h1>Pranav Malhotra</h1>
              <p>
                I'm a software engineer specializing in building and designing innovative digital experiences. Currently,
                I am focused on learning about Angular, MongoDB and Firebase by implementing them in my projects.{' '}
                <Link to="/projects" className="link-to-projects">Here</Link> are some of those.
              </p>
              <p> Post Graduate Certificate(Co-Op) - Fanshawe College</p>
              <br />
              <p>HTML | CSS | JavaScript | React | React Native | SQL | AWS | UI/UX | JENKINS | JIRA | AGILE</p>
              <div className="cta-buttons">
                <Link to="/projects" className="btn">View Projects</Link>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default Home;
