// Home.jsx
import React from 'react';
import { Link as RouterLink } from 'react-router-dom';
import { Box, Button, Container, Heading, Link, Text, VStack } from '@chakra-ui/react';
import { motion } from 'framer-motion';

const MotionBox = motion(Box);

const Home = () => {
  return (
    <MotionBox
      bg="black"
      color="white"
      minH="90vh"
      display="flex"
      alignItems="center"
      justifyContent="center"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
    >
      <Container
        maxW="container.md"
        p={8}
        boxShadow="xl"
        borderRadius="md"
        borderColor="#04D9FF"
        borderWidth="1px"
      >
        <VStack spacing={6} align="start">
          <Text id="intro" color="gray.400">Hi, my name is</Text>
          <Heading as="h1" size="2xl" color="#04D9FF">Pranav Malhotra</Heading>
          <Text fontSize="lg" lineHeight="tall" color="gray.400">
            I'm a software engineer specializing in building and designing innovative digital experiences. Currently, I am focused on learning about NextJS, TypeScript, Postgres, NodeJS, Angular, MongoDB and Firebase by implementing them in my projects.{' '}
            <Link as={RouterLink} to="/projects" color="#04D9FF">Here</Link> are some of those.
          </Text>
          <Text fontSize="lg" color="gray.400">
            HTML | CSS | JavaScript | React | React Native | SQL | AWS | UI/UX | JENKINS | JIRA | AGILE
          </Text>
          <Button as={RouterLink} to="/experience" colorScheme="teal" variant="outline">
            Learn More
          </Button>
        </VStack>
      </Container>
    </MotionBox>
  );
}

export default Home;
